import React from 'react';
import AuthService from './AuthService.jsx'
import { Redirect } from 'react-router-dom';
import axios from 'axios';

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            login: '',
            password: '',
            errorMessage: ''
        }
        this.Auth = new AuthService();
    }

    inputChange = e => this.setState({ [e.target.name]: e.target.value })

    formIsValid = () => {

        let { login, password } = this.state;

        if (!login || !password) {
            this.setState({ errorMessage: 'Заполните все данные' });
            return false;
        }

        return true
    }

    onLoginSubmit = (e) => {
        e.preventDefault();

        if (!this.formIsValid())
            return false;

        this.Auth.login(this.state.login, this.state.password)
            .then(res => {
                this.props.history.replace('/root/main');
            })
            .catch(err => {
                this.setState({ errorMessage: 'Неправильный логин или пароль' });
            })
    }

    render() {
        let { login, password, errorMessage } = this.state;

        if (this.Auth.loggedIn())
            return <Redirect to='/root' />

        return <div className="container">
            <div className="row justify-content-center">
                <div className="col-6">
                    <div className="text-center" style={{ paddingTop: '10vh' }}>
                        <img className="center-block" src="data:image/svg+xml;base64,"
                            style={{ maxWidth: '90%' }}
                    />

                        <img src="data:image/svg+xml;utf8;base64," />                    </div>
                    <form
                        onSubmit={this.onLoginSubmit}
                        >
                        <div className="form-group">
                            <label className="col-form-label-lg">Логин</label>
                            <input
                                name="login"
                                type="text"
                                className="form-control form-control-lg"
                                placeholder="Enter your login"
                                value={login}
                                onChange={this.inputChange} />
                            <label className="col-form-label-lg">Пароль</label>
                            <input
                                name="password"
                                type="password"
                                className="form-control form-control-lg"
                                placeholder="Enter your password"
                                value={password}
                                onChange={this.inputChange} />
                            <p className="form-text text-danger">
                                {errorMessage}
                            </p>
                        </div>
                        <button type="submit" className="btn btn-outline-dark btn-block btn-lg mb-2">Войти</button>
                    </form>
                </div>
            </div>            
        </div>
    }
}